import React from 'react';
import bgImg from '../Images/bg1.jpg'; // Ensure this path is correct

function Home() {
  return (
    <div className='flex flex-col lg:flex-row items-center justify-center h-screen bg-cover' style={{ backgroundImage: `url(${bgImg})` }}>
      <div className='lg:w-1/2 bg-dark flex flex-col justify-center items-center p-8'>
        <div className='font-bold text-4xl text-white mb-4'>
          <span>UNLOCK</span>
          <span className='text-yellow-500'>YOUR STYLE</span>
        </div>
        <div className='text-white text-center'>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
          </p>
          <button className="bg-black text-white px-4 py-2 rounded-full mt-4 font-bold">Download CV</button>
        </div>
      </div>
      {/* Show this only on small screens */}
      <div className='lg:w-1/2 lg:block hidden'>
        {/* Placeholder for content or empty */}
      </div>
    </div>
  );
}

export default Home;
