import React, { useState } from "react";

export default function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="bg-[#8173B2] text-white">
      <div className="container mx-auto flex justify-between items-center py-4 px-6 lg:px-8">
        <div className="flex items-center">
          <h1 className="text-lg font-semibold">Logo</h1>
        </div>
        <div className="lg:flex items-center space-x-8">
          <nav className={`lg:flex items-center space-x-8 ${isMenuOpen ? 'hidden' : ''}`}>
            <a href="#" className="hover:text-gray-300">Home</a>
            <a href="#" className="hover:text-gray-300">About Us</a>
            <a href="#" className="hover:text-gray-300">Portfolio</a>
            <a href="#" className="hover:text-gray-300">Contact</a>
          </nav>
          <div className="lg:hidden">
            {/* Mobile menu button */}
            <button className="text-white focus:outline-none" onClick={toggleMenu}>
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                {isMenuOpen ? (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                ) : (
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7"></path>
                )}
              </svg>
            </button>
          </div>
        </div>
        {isMenuOpen && (
          <div className="lg:hidden mt-4">
            {/* Mobile menu */}
            <select
              className="block appearance-none w-full bg-[#8173B2] border border-white text-white py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
              onChange={(e) => setIsMenuOpen(false)}
            >
              <option value="#">Home</option>
              <option value="#">About Us</option>
              <option value="#">Portfolio</option>
              <option value="#">Contact</option>
            </select>
          </div>
        )}
      </div>
    </div>
  );
}
