import React from 'react';
import imgs from '../Images/bg2.jpg';

export default function About() {
  return (
    <div className='flex'>
      <div className='left flex-1 flex justify-center items-center'>
        <div className='flex justify-center items-center'>
          <img src={imgs} alt='no image show' style={{ width: '300px', height: '500px' }} />
        </div>
      </div>
      <div className='right flex-1 content-center ml-4 flex-col space-y-2'>
        <p>About Company</p>
        <div className='font-bold text-4xl flex-col flex space-y-2 text-black'>
          <span>WHERE STYLE</span>
          <span>MEETS COMFORT!</span>
        </div>
      </div>
    </div>
  );
}
